import React, { useEffect } from "react";
import axios from "axios";

import {
  StyleSheet,
  Text,
  View,
  Image,
  ImageBackground,
  TouchableOpacity,
} from "react-native";
import { AntDesign as Icon } from "@expo/vector-icons";

import { useDispatch } from "react-redux";
import { setSignIn } from "../redux/slices/authSlice";

import TextInput from "../components/TextInput";
import Button from "../components/Button";

const LoginScreen = () => {
  const dispatch = useDispatch();

  const handleLogin = () => {
    const user = {
      isLoggedIn: true,
      email: "jdoe@test.com",
      userName: "johnDoe",
    };

    dispatch(setSignIn(user));
  };

  // const getData = async () => {
  //   await axios
  //     .get("/home")
  //     .then((res) => {
  //       console.log(res);
  //     })
  //     .catch((error) => {
  //       console.log(error);
  //     });
  // };

  // useEffect(() => {
  //   getData();
  // }, []);

  return (
    <View style={styles.container}>
      <View style={styles.logoArea}>
        <Image
          style={styles.logo}
          source={{
            uri: "https://d5nunyagcicgy.cloudfront.net/external_assets/hero_examples/hair_beach_v391182663/original.jpeg",
          }}
          resizeMode="contain"
        />
        <Text style={styles.subText}>Sign in to continue</Text>
      </View>
      <View style={styles.form}>
        <View style={styles.formGroup}>
          <TextInput
            icon="mail"
            placeholder="Enter your email or phone"
            autoCapitalize="none"
            autoCompleteType="email"
            keyboardType="email-address"
            keyboardAppearance="dark"
            returnKeyType="next"
            returnKeyLabel="next"
          />
        </View>
        <View style={styles.formGroup}>
          <TextInput
            icon="key"
            placeholder="Enter your password"
            secureTextEntry
            autoCompleteType="password"
            autoCapitalize="none"
            keyboardAppearance="dark"
            returnKeyType="go"
            returnKeyLabel="go"
          />
        </View>
        <View style={styles.buttonGroup}>
          <Button label="Login" onPress={handleLogin} />
        </View>
      </View>
      <View style={{ padding: 8 }}>
        <Text style={styles.helpText}>
          Don't have an account yet? Register now
        </Text>
        <Text style={styles.helpText}> Forgot your password? </Text>
        <Text style={styles.helpText}>
          <Icon name="customerservice" size={24} color="black" /> Sign in to
          continue
        </Text>
      </View>
    </View>
  );
};

export default LoginScreen;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "#F2F2F2",
    padding: 30,
  },
  logoArea: {
    justifyContent: "center",
    alignItems: "center",
  },
  logo: {
    padding: 20,
    width: 350,
    height: 100,
    marginBottom: 30,
  },
  subText: {
    backgroundColor: "rgba(96, 194, 172, 0.10)",
    paddingTop: 5,
    paddingBottom: 5,
    paddingRight: 15,
    paddingLeft: 15,
    borderRadius: 15,
    marginBottom: 30,
    fontSize: 14,
    lineHeight: 20,
    color: "#606564",
  },
  helpText: {
    color: "#838487",
    fontSize: 20,
    textDecorationLine: "underline",
    textAlign: "center",
    alignItems: "center",
    margin: 5,
  },
  form: {
    width: "100%",
  },
  formGroup: {
    paddingHorizontal: 32,
    marginBottom: 16,
    width: "100%",
  },
  buttonGroup: {
    justifyContent: "center",
    paddingHorizontal: 32,
    marginBottom: 16,
    width: "100%",
    alignItems: "center",
    marginTop: 15,
  },
  title: {
    margin: 24,
    fontSize: 24,
    fontWeight: "bold",
    textAlign: "center",
  },
  error: {
    margin: 8,
    fontSize: 14,
    color: "red",
    fontWeight: "bold",
  },
  input: {
    height: 50,
    paddingHorizontal: 8,
    width: "100%",
    borderColor: "#ddd",
    borderWidth: 1,
    backgroundColor: "#fff",
  },
  btn: {
    backgroundColor: "blue",
    paddingHorizontal: 50,
    paddingVertical: 10,
    borderRadius: 10,
  },
  text: {
    color: "white",
    fontSize: 20,
  },
});
