import { View, Text, StyleSheet, Image, ImageBackground } from 'react-native'
import React from 'react'
import Button from '../components/Button';
import logo from "../images/bdrentz.png";


const image = { uri: "https://bdrentz.com/media/files/vendor/5/komatsu-excavator.jpg" };
export default function Home() {

    const handleSubmit = ()=>{
        console.log("submitted");
    }
  return (
    <View style={styles.container}>
    <ImageBackground source={image} resizeMode="cover" style={{flex:1}}>
            <View style={styles.headerTopArea}>
                <View>
                   <Image source={logo} width="240" height="100" />
                </View>   
                <View>
                    <Text style={styles.headerTitle}>THE LARGEST</Text>
                    <Text style={styles.headerTitle}>EQUIPMENT RENTAL NETWORK</Text>
                </View>   
            </View>
            <View style={styles.headerButtomArea}>
              
                 <View style={styles.buttonContainer}>
                   <Button style={styles.cusLogin} label="LOGIN AS CUSTOMAR" bgColor="#f6921e" color="#fff" onPress={handleSubmit} />
                   <Button label="LOGIN AS VENDOR" bgColor="#fff" color="#000" onPress={handleSubmit} />
                 </View>
         
            </View>
    </ImageBackground>
    </View>
  )
}

const styles = StyleSheet.create({
    container:{
        flex:1,
    },
    headerTopArea:{
        flex:0.7,
        justifyContent:"space-around",
        backgroundColor:"#fff",
        alignItems:"center",
        borderBottomEndRadius:40,
        borderBottomStartRadius:40
    },
    headerButtomArea:{
        flex:1,
        alignItems:"center",
        justifyContent:"center"
    },
    buttonContainer:{
       gap:35
    },
    headerTitle:{
       fontSize:20,
       textAlign:"center"
    }
})
