import { View, Text } from 'react-native'
import React from 'react'

export default function Button( label, onPress,color,bgColor) {
  return (
    <TouchableOpacity
      style={{
        borderRadius: 30,
        height: 60,
        width: 245,
        justifyContent: "center",
        alignItems: "center",
        backgroundColor: bgColor,
      }}
      activeOpacity={0.7}

    >
      <Text
        style={{ fontSize: 20, color: color, textTransform: "uppercase" }}
      >
        {label}
      </Text>
    </TouchableOpacity>
  )
}
