import {
  View,
  Text,
  StyleSheet,
  ImageBackground,
  Image,
  TextInput,
  SafeAreaView,
  ScrollView,
} from "react-native";
import React from "react";
import HeaderBgImage from "../images/loginBg.jpg";
import { AntDesign as Icon, Entypo, Fontisto } from "@expo/vector-icons";
import Button from "../components/button/Button";
import shopLogo from "../images/icon-02.png";
import bdLogo from "../images/icon-01.png";
import cardLogo from "../images/icon-03.png";
// import DateTimePicker from '@react-native-community/datetimepicker';

export default function HireNow(props) {
  const handleSubmit = () => {
    props.navigation.navigate("Quotation");
  };
  return (
    <SafeAreaView style={styles.container}>
      <ScrollView style={styles.scrollView}>
        <View style={styles.hireTopCotainer}>
          <ImageBackground
            source={HeaderBgImage}
            resizeMode="cover"
            style={{ flex: 1 }}
          >
            <View style={styles.titleContainer}>
              <Text style={styles.hireTitle}>THE LARGEST</Text>
              <Text style={styles.hireTitle}>EQUIPMENT RENTAL NETWORK</Text>
            </View>
          </ImageBackground>
        </View>
        <View style={styles.hireMiddleCotainer}>
          <View>
            <Text style={styles.hireTitle}>HIRE NOW</Text>
          </View>
          <View style={styles.hireInputContainer}>
            <Icon
              name="search1"
              size={25}
              color="gray"
              style={styles.inputIcon}
            />
            <TextInput style={styles.hireInput} placeholder="Equipment Name" />
          </View>
          <View style={styles.hireInputContainer}>
            <Entypo
              name="location-pin"
              size={25}
              color="gray"
              style={styles.inputIcon}
            />
            <TextInput style={styles.hireInput} placeholder="job Location" />
          </View>
          <View style={styles.hireInputContainer}>
            <Fontisto
              name="date"
              size={25}
              color="gray"
              style={styles.inputIcon}
            />
            <TextInput
              style={styles.hireInput}
              type="date"
              placeholder="Start Date"
            />
          </View>
          <View style={styles.hireInputContainer}>
            <Fontisto
              name="date"
              size={25}
              color="gray"
              style={styles.inputIcon}
            />
            <TextInput
              style={styles.hireInput}
              type="date"
              placeholder="End Date"
            />
          </View>
          <View style={styles.bookBtnContainer}>
            <Button
              onPress={() => handleSubmit()}
              style={styles.bookButton}
              label="BOOK NOW"
              bgColor="#f6921e"
              color="#fff"
            />
          </View>
        </View>
        <View style={styles.hireBottomArea}>
          <View>
            <Image style={styles.bottomLogo} source={shopLogo} />
          </View>
          <View style={styles.middleLogo}>
            <Image style={styles.bottomLogo} source={bdLogo} />
          </View>
          <View>
            <Image style={styles.bottomLogo} source={cardLogo} />
          </View>
        </View>
      </ScrollView>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#f6921e",
  },
  scrollView: {
    backgroundColor: "#fff",
  },
  hireTopCotainer: {
    flex: 0.5,
    borderBottomRightRadius: 40,
    borderBottomLeftRadius: 40,
    overflow: "hidden",
  },
  hireMiddleCotainer: {
    flex: 1,
    alignItems: "center",
    marginTop: 30,
    gap: 25,
  },
  titleContainer: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
  },
  hireTitle: {
    fontSize: 15,
    textAlign: "center",
    color: "#fff",
    // paddingTop:10,
    // paddingBottom:10
  },
  hireInputContainer: {
    flexDirection: "row",
    backgroundColor: "#fff",
    borderRadius: 10,
  },
  hireInput: {
    width: 288,
    height: 40,
    paddingLeft: 10,
    fontSize: 15,
    color: "gray",
  },
  inputIcon: {
    paddingTop: 7,
    paddingLeft: 10,
    paddingRight: 10,
    fontWeight: "bold",
    borderRightColor: "gray",
    borderRightWidth: 1,
  },
  bookBtnContainer: {
    marginTop: 35,
    borderColor: "#fff",
    borderWidth: 2,
    borderRadius: 10,
  },
  bottomLogo: {
    width: 50,
    height: 50,
  },
  hireBottomArea: {
    flex: 0.1,
    flexDirection: "row",
    alignItems: "flex-end",
    justifyContent: "space-around",
    backgroundColor: "#fff",
    paddingTop: 10,
  },
  middleLogo: {
    backgroundColor: "#dfe6e9",
    padding: 10,
    borderRadius: 50,
  },
});
